from fastapi import FastAPI

app = FastAPI()

@app.get("/")
def read_root():
    """Root endpoint that returns a welcome message."""
    return {"Hello": "World"}

@app.get("/api/docs")
def get_api_docs():
    """API documentation endpoint."""
    return {"documentation": "API documentation can be accessed at /docs"}

# Swagger UI and redoc documentation are automatically generated by FastAPI
# and can be found at /docs and /redoc endpoints respectively.

if __name__ == "__main__":
    import uvicorn
    uvicorn.run(app, host="0.0.0.0", port=8000)